			IF(p_typeId <> 0)THEN
				SET @query = REPLACE(@query, '_add', 'AND t.id = p_typeId');
				SET @query = REPLACE(@query, 'p_typeId', p_typeId);
			END IF;
			IF(p_userId <> 0)THEN
				SET @query = REPLACE(@query, 'p_userId', p_userId);
			END IF;
			IF(p_month <> 0)THEN
				SET @query = REPLACE(@query, 'p_month', p_month);
			END IF;
		PREPARE stmt FROM @query;
		EXECUTE stmt;
		SET p_result = 1;
	ELSE
		SET p_result = -2;
	END IF;
ELSE
	SET p_result =-1;
END IF;
